var lines = input.Split("\r\n");
var safes = 0;

foreach (var line in lines)
{
    if (IsSafe(line))
    {
        safes++;
    }
}

Console.WriteLine($"Safe lines: {safes}");

static bool IsSafe(string line)
{
    var matches = Regex.Matches(line, "[0-9]+");
    var numbers = new List<int>();

    for (int i = 0; i < matches.Count; i++)
    {
        numbers.Add(int.Parse(matches[i].Value));
    }

    var valid = ValidateSeries(numbers);

    // Problem 2 allow 1 fault
    var validWithRemoval = false;
    
    if (!valid)
    {
        for (int i = 0; i < numbers.Count; i++)
        { 
            var removeOne = new List<int>();
            removeOne.AddRange(numbers);
            removeOne.RemoveAt(i);
            validWithRemoval = ValidateSeries(removeOne);

            if (validWithRemoval) 
            {
                break;
            }
        }
    }

    return valid || validWithRemoval;
}

static bool ValidateSeries(List<int> numbers)
{
    bool increase = numbers[0] < numbers[1];
    var valid = true;

    for (int i = 0; i < (numbers.Count - 1); i++)
    {
        valid = valid && ValidatePair(numbers[i], numbers[i + 1], increase);
    }

    return valid;
}

static bool ValidatePair(int a, int b, bool increase)
{
    var diff = Math.Abs(a - b);

    if (diff < 1 || diff > 3)
    {
        return false;
    }

    if (increase && a >= b)
    {
        return false;
    }

    if (!increase && a <= b)
    {
        return false;
    }

    return true;
}