var forwards = 0;
var backwards = 0;

// Count forwards
foreach(var line in lines)
{
    var matchesForwards = Regex.Matches(line, "XMAS");
    forwards += matchesForwards.Count();

    var matchesBackwards = Regex.Matches(new string(line.Reverse().ToArray(), 0, line.Reverse().Count()), "XMAS");
    backwards += matchesBackwards.Count();
}

Console.WriteLine($"Forwards: {forwards}, Backwards {backwards}");

for (int l = 0; l < lines.Count(); l++)
{
    for (int c = 0; c < lines[l].Length; c++)
    {
        CheckForChar()
    }
}

