v>^><<^v<v>><v<v^>vv<<<<v^>^v<v<^v><^<>>>^>>>>><v^vv<<>>v<<>><^>vv^><^><vv>^>><v^^^>>^<vv^vv>".ToArray();

(int row, int col) robotPos = FindStart();

for (var i = 0; i < movements.Length; i++)
{
    if(movements[i] == '\r' || movements[i] == '\n')
    {
        continue;
    }

    var dir = movements[i];

    if (dir == '^')
    {
        (int row, int col)? space = FindSpace(-1, 0, robotPos.row, robotPos.col);
        
        if(space is not null && space.HasValue)
        {
            var end = space.Value;

            for(var j = end.row; j <= robotPos.row-2; j++)
            {
                grid[j][robotPos.col] = grid[j+1][robotPos.col];
            }

            grid[robotPos.row][robotPos.col] = '.';
            grid[robotPos.row - 1][robotPos.col] = '@';
            robotPos = (robotPos.row - 1, robotPos.col);
        }
    }

    if(dir == 'v')
    {
        (int row, int col)? space = FindSpace(1, 0, robotPos.row, robotPos.col);

        if (space is not null && space.HasValue)
        {
            var end = space.Value;

            for (var j = robotPos.row+2; j <= end.row; j++)
            {
                grid[j][robotPos.col] = grid[j - 1][robotPos.col];
            }

            grid[robotPos.row][robotPos.col] = '.';
            grid[robotPos.row+1][robotPos.col] = '@';
            robotPos = (robotPos.row + 1, robotPos.col);
        }       
    }

    if (dir == '<')
    {
        (int row, int col)? space = FindSpace(0, -1, robotPos.row, robotPos.col);

        if (space is not null && space.HasValue)
        {
            var end = space.Value;

            for (var j = end.col; j <= robotPos.col-2; j++)
            {
                grid[robotPos.row][j] = grid[robotPos.row][j + 1];
            }

            grid[robotPos.row][robotPos.col] = '.';
            grid[robotPos.row][robotPos.col - 1] = '@';
            robotPos = (robotPos.row, robotPos.col - 1);
        }
    }

    if (dir == '>')
    {
        (int row, int col)? space = FindSpace(0, 1, robotPos.row, robotPos.col);

        if (space is not null && space.HasValue)
        {
            var end = space.Value;
            
            for (var j = robotPos.col+2; j <= end.col; j++)
            {
                grid[robotPos.row][j] = grid[robotPos.row][j - 1];
            }

            grid[robotPos.row][robotPos.col] = '.';
            grid[robotPos.row][robotPos.col + 1] = '@';
            robotPos = (robotPos.row, robotPos.col + 1);
        }
    }
}


var score = 0;
for (var i = 0; i < grid.Length; i++)
{
    for (var j = 0;  j < grid[i].Length; j++)
    {
        if (grid[i][j] == 'O')
        {
            score += 100 * i + j;
        }
    }
}

Console.WriteLine($"Score: {score}");

(int row, int col)? FindSpace(int dRow, int dCol, int row, int col)
{
    if(row + dRow < 1 || row + dRow >= grid.Length - 1 || col + dCol < 1 || col + dCol >= grid[0].Length - 1)
    {
        return null;
    }

    if (grid[row+dRow][col+dCol] == '#')
    {
        return null;
    }

    if (grid[row+dRow][col+dCol] == '.')
    {
        return (row+dRow, col+dCol);
    }

    if (grid[row + dRow][col + dCol] == 'O')
    {
        return FindSpace(dRow, dCol, row + dRow, col + dCol);
    }

    Console.WriteLine("Should have hit wall");
    return null;
}

(int row, int col) FindStart()
{
    for (var i = 0; i < grid.Length; i++)
    {
        for (var j = 0; j < grid[i].Length; j++)
        {
            if(grid[i][j] == '@')
            {
                return (j,i);
            }
        }
    }

    Console.WriteLine("Start not found");
    return (-1, -1);
}

int GetGpsValue(int row, int col)
{
    return row * 100 + col;
}